mapper.insert:
select @orderNumber:=(select ifnull(max(oNum),0)+1 from corder as onum);
select @deliveryPK:=(select ifnull(max(DPK),0)+1 from delivery as dp);
select @paymentNumber:=(select ifnull(max(pNum),0)+1 from payment as pNum);
insert into corder (oNum,oDate,oPrice,oStock,oAddr1,oAddr2,oAddr3,oAddr4,oStatus,oName,oPhone,member_mId,goods_gNum) values (@orderNumber,
		default,#{oPrice},#{oStock},#{oAddr1},#{oAddr2},#{oAddr3}
		,#{oAddr4},'주문완료',#{oName},#{oPhone},#{member_mId},#{goods_gNum});

insert  into payment values
		(@paymentNumber,#{oPrice},default,'신용카드',
		@orderNumber);
insert into delivery
value(@deliveryPK,'배송준비중',#{oAddr1},
#{oAddr2},#{oAddr3},#{oAddr4},#{dInfo},LPAD((select ifnull(max(DPK),0)+1 from delivery as dp2),
12 ,concat(DATE_FORMAT(current_timestamp,'%Y%m%d'),'000')),default,@orderNumber);


	SELECT @orderNumber

mapper.select:
select o.*,d.dInfo,d.dNum,p.pNum,p.ppayment,g.gName from corder o,payment p,
delivery d,goods g where o.oNum = ${value} and p.corder_oNum = ${value}
 and d.corder_oNum = ${value} and g.gnum = o.goods_gNum;
